INSERT INTO monumento (id, cod_pais, pais, ciudad, longitud, latitud, nombre, descripcion, imagen) VALUES (1, 'ES', 'España', 'Bilbao', -2.92528, 43.26271, 'Puppy', 'Es una icónica escultura floral moderna con forma de cachorro realizada por el artista estadounidense Jeff Koons en 1992 que está ubicada frente al Museo Guggenheim.', 'https://cms.guggenheim-bilbao.eus/uploads/2021/03/Jeff-Koons-Puppy-1992.jpg');
INSERT INTO monumento (id, cod_pais, pais, ciudad, longitud, latitud, nombre, descripcion, imagen) VALUES (2, 'ES', 'España', 'Sevilla', -5.97317, 37.38283, 'Giralda', 'Es la torre campanario de la catedral de Santa María de la Sede de la ciudad de Sevilla. La parte inferior de la torre corresponde al alminar de la antigua mezquita de la ciudad, de finales del siglo XII, levantado en época almohade, mientras que la parte superior es una construcción sobrepuesta en el siglo XVI, en época cristiana, para albergar el cuerpo de campanas.', 'https://www.guiasevilla.com/f/espana/sevilla/guia/giralda-m.jpg');
INSERT INTO monumento (id, cod_pais, pais, ciudad, longitud, latitud, nombre, descripcion, imagen) VALUES (3, 'NZ', 'Nueva Zelanda', 'Christchurch', 172.63333, -43.53333, 'Estatua de Cook', 'Se localiza en la Plaza Victoria, Christchurch, conmemora los tres viajes de James Cook a Nueva Zelanda. La estatua, esculpida por William Trethewey, se dio a conocer el 10 de agosto de 1932 por el Gobernador General, Lord Bledisloe.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/b/bb/Captain_Cook_statue%2C_Christchurch.jpg/640px-Captain_Cook_statue%2C_Christchurch.jpg');
INSERT INTO monumento (id, cod_pais, pais, ciudad, longitud, latitud, nombre, descripcion, imagen) VALUES (4, 'NO', 'Noruega', 'Larvik', 10.03517, 59.05328, 'Minnehallen', 'El monumento fue encargado por el Parlamento noruego después de la Primera Guerra Mundial para conmemorar a los marineros noruegos caídos en la guerra. Fue inaugurado por el rey Haakon VII y posteriormente se convirtió en el monumento nacional que conmemora a los marineros caídos tanto en la Primera como en la Segunda Guerra Mundial.', 'https://i.ytimg.com/vi/zm0GQ2xoTL4/maxresdefault.jpg');
INSERT INTO monumento (id, cod_pais, pais, ciudad, longitud, latitud, nombre, descripcion, imagen) VALUES (5, 'DK', 'Dinamarca', 'Nuuk', -51.72157, 64.18347, 'Estatua de Hans Egede', 'Es un monumento destacado en la ciudad de Nuuk, la capital de la isla y territorio de Groenlandia, una dependencia autónoma de Dinamarca. Conmemora la labor del misionero luterano de origen danés y noruego Hans Egede que fundó Nuuk en el año 1728.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/56/Egede_nuuk.JPG/320px-Egede_nuuk.JPG');
